import { useContext } from 'react'
import Navbar from "../../Components/nav/nav";
import NavbarAdmin from "../../Components/nav/navAdmin"
import styles from "../../Css/home.module.css";
import product1 from "../../Images/product1.jfif";
import product2 from "../../Images/product2.jpg";
import {Link} from "react-router-dom"
import { UserContext } from "../../context/userContext";

function Home() {

  const [state] = useContext(UserContext);

  let nav = ""

  const checkAuth = () => {
    if (state.isLogin === true) {
      if(state.user.user.status == "admin"){
        nav = true
      }else{
        nav = false
      }
    }
    console.log(state.user.user.status)
  };
  checkAuth()

  return (
    <div className={styles.homeBg}>
{nav ? <Navbar /> : <NavbarAdmin/>}
      
      <div className={styles.productTitle}>
        <h1>Product</h1>
      </div>
      <div className={styles.productHeader}>
        <div className={styles.productCard}>
          <div className={styles.productImage}>
            <img src={product1} alt="" />
          </div>
          <div className={styles.productName}>
            <Link to="/detail"><h2>KYT Falcon FR</h2></Link>
          </div>
          <div className={styles.productDetail}>
            <small>Rp.700.000</small>
            <small>Stock: 235</small>
          </div>
        </div>
        <div className={styles.productCard}>
          <div className={styles.productImage}>
            <img src={product2} alt="" />
          </div>
          <div className={styles.productName}>
            <h2>Fredconn Intercom</h2>
          </div>
          <div className={styles.productDetail}>
            <small>Rp.895.000</small>
            <small>Stock: 60</small>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
