import React, { useState, useContext } from "react";
import { useNavigate } from "react-router";
import { useMutation } from "react-query";
import { API } from "../../config/api";
import { useQuery } from "react";
import styles from "../../Css/category.module.css";
import Navbar from "../nav/navAdmin";

function EditCategory() {
  let navigate = useNavigate();
  const [category, setCategory] = useState("");

  const title = "Add Category";
  document.title = "DumbMerch | " + title;

  let { data: categories } = useQuery("productData", async () => {
    const response = await API.get("/categories");
    console.log(response.data.data);
    return response.data.data;
  });

  const handleChange = (e) => {
    setCategory(e.target.value);
  };

  const handleSubmit = useMutation(async (e) => {
    try {
      e.preventDefault();

      // Configuration
      const config = {
        headers: {
          "Content-type": "application/json",
        },
      };

      // Data body
      const body = JSON.stringify({ name: category });

      const catData = await API.get("/categories");
      console.log(catData);

      // Insert category data
      const response = await API.patch("/category", body, config);
      console.log(response);

      navigate("/category");
    } catch (error) {
      console.log(error);
    }
  });
  return (
    <div className={styles.editContainer}>
      <Navbar />
      <div className={styles.editCatContainer}>
        <h1>Edit category</h1>
        <p>From:</p>
        {categories?.length !== 0 && (
          <div>
            <form action="">
              <select name="existCat" id="EC"></select>
              {categories?.map((item, index) => (
                <option value={item.name}></option>
              ))}
              <button>Save</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

export default EditCategory;
