import styles from "../../Css/product.module.css";
import Navbar from "../nav/navAdmin";

function editProduct() {
  const title = 'Product admin';
  document.title = 'DumbMerch | ' + title;

  let navigate = useNavigate();
  const { id } = useParams();

  const [categories, setCategories] = useState([]); //Store all category data
  const [categoryId, setCategoryId] = useState([]); //Save the selected category id
  const [preview, setPreview] = useState(null); //For image preview
  const [product, setProduct] = useState({}); //Store product data

  // For handle if category selected
  const handleChangeCategoryId = (e) => {
    const id = e.target.value;
    const checked = e.target.checked;

    if (checked) {
      // Save category id if checked
      setCategoryId([...categoryId, parseInt(id)]);
    } else {
      // Delete category id from variable if unchecked
      let newCategoryId = categoryId.filter((categoryIdItem) => {
        return categoryIdItem != id;
      });
      setCategoryId(newCategoryId);
    }
  };

  // Handle change data on form
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]:
        e.target.type === 'file' ? e.target.files : e.target.value,
    });

    // Create image url for preview
    if (e.target.type === 'file') {
      let url = URL.createObjectURL(e.target.files[0]);
      setPreview(url);
    }
  };

  useEffect(() => {
    const newCategoryId = product?.categories?.map((item) => {
      return item.id;
    });

    setCategoryId(newCategoryId);
  }, [product]);

  return (
    <div>
        <div className={styles.addContainer}>
      <NavbarAdmin />
      <div className={styles.addCatContainer}>
        <h1>Edit Product</h1>
        <form onSubmit={(e) => handleSubmit.mutate(e)}>
        {preview && (
                <div>
                  <img
                    src={preview}
                    style={{
                      maxWidth: '9rem',
                      maxHeight: '150px',
                      objectFit: 'cover',
                      marginBottom: '1rem',
                      borderRadius: '0.3rem'
                    }}
                    alt={preview}
                  />
                </div>
              )}
        <div className={styles.addUpCon}>
        
          <label htmlFor="upImge">
            <p>Upload Image</p>
            <input type="file" id="upImge" name="image" onChange={handleChange} hidden />
          </label>
        </div>
        <input type="text" name="name" onChange={handleChange} placeholder="Product name..." />
        <textarea name="desc" onChange={handleChange} id="desc">
        </textarea>
        
        <input type="number" name="price" onChange={handleChange} placeholder="Product price..." />
        <input type="text" name="qty" onChange={handleChange} placeholder="Product quantity..." />
        <h4 style={{
              color:'white',
              fontWeight:'bold;'
            }}>Category</h4>
            <div style={{
            display: 'flex',
            width: '100%',
          }}>
        {categories?.map((item, index) => (
          
                  <div>
                    <label style={
                    {display: 'flex',
                     gap: '0.5rem',
                     color: 'white',
                     fontSize: '1rem',
                     width: '9.5rem',
                     alignItems:'center',
                     padding: '0',
                     margin:'1rem 0rem'
                  }

                  } key={index}>
                    <input
                      type="checkbox"
                      name="category"
                      value={item?.id}
                      onClick={handleChangeCategoryId}
                    /><p>{item?.name}</p>
                  </label>
                    </div>
          
                ))}
                </div>
        <button type="submit">Edit Product</button>
        </form>
      </div>
    </div>
    </div>
  );
}

export default editProduct;
