import styles from "../../Css/category.module.css";
import Navbar from "../../Components/nav/navAdmin";
import Modal from "../../Components/category/modal";
import Edit from "../../Components/category/edit";
import Add from "../../Components/category/add"
import { useState } from "react";

function Category() {
  let [modal, setModal] = useState(false);
  let [edit, setEdit] = useState(false);
  let [add, setAdd] = useState(false);

  let {data: category, isLoading} = useQuery('productData', async () => {
    const response = await API.get('/category')
    console.log(response.data.data)
    return response.data.data.category
  })

  return (
    <div>
      <Navbar />
      <div className={styles.container}>
        <div className={styles.catContainer}>
          <table className={styles.contentTable}>
            <thead>
              <tr>
                <th>No</th>
                <th>Category Name</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {category?.length !==0 && <tbody>
                {category?.map((item,index) => (
                  <tr>
                  <td>{category.id}</td>
                  <td>{category.name}</td>
                  <td>
                  <button
                      className={styles.edit}
                      onClick={() => {
                        setAdd(true);
                      }}
                    >
                      Add
                    </button>
                    <button
                      className={styles.edit}
                      onClick={() => {
                        setEdit(true);
                      }}
                    >
                      Edit
                    </button>
                    <button
                      className={styles.delete}
                      onClick={() => {
                        setModal(true);
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
                ))}
              </tbody> }
            </tbody>
          </table>
        </div>
      </div>
      <div>
      {add && <Add />}
      {edit && <Edit />}
      {modal && <Modal closeModal={setModal} />}
      </div>
    </div>
  );
}

export default Category;
