import styles from "../../Css/product.module.css";
import Navbar from "../../Components/nav/navAdmin";
import Edit from "../../Components/product/product"
import {API} from "../../config/api"
import Modal from "../../Components/category/modal"
import {useQuery} from 'react-query'
import {useState} from "react"

function Product() {

let [edit, setEdit] = useState(false)

let [modal, setModal] = useState(false)

let {data: product, isLoading} = useQuery('productData', async () => {
  const response = await API.get('/products')
  console.log(response.data)
  return response.data
})

console.log(isLoading)

  return (
    <div>
      <Navbar />
      <div className={styles.container}>
        <div className={styles.catContainer}>
          <table className={styles.contentTable}>
            <thead>
              <tr>
                <th>No</th>
                <th>Photo</th>
                <th>Product Name</th>
                <th>Product Desc</th>
                <th>Price</th>
                <th>Qty</th>
                <th>Action</th>
              </tr>
            </thead>
            {product?.length !==0 && <tbody>
                {product?.map((item,index) => (
                  <tr>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>
                    <button
                      className={styles.edit}
                      onClick={() => {
                        setEdit(true);
                      }}
                    >
                      Edit
                    </button>
                    <button
                      className={styles.delete}
                      onClick={() => {
                        setModal(true);
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
                ))}
              </tbody> }
          </table>
        </div>
      </div>
      {edit && <Edit/>}
      {modal && <Modal closeModal={setModal}/>}
    </div>
  );
}

export default Product;
