import styles from "../../Css/profile.module.css";
import Navbar from "../../Components/nav/nav";
import { useState, useContext, useEffect } from 'react'
import { UserContext } from '../context/userContext'
import { useQuery } from 'react-query';
import { API } from '../config/api';

function Profile() {

  const title = 'Profile';
  document.title = 'DumbMerch | ' + title;

  const [state] = useContext(UserContext);

  let { data: profile } = useQuery('profileCache', async () => {
    const response = await API.get('/profile');
    return response.data.data;
  });

  let { data: transactions } = useQuery('transactionsCache', async () => {
    const response = await API.get('/transactions');
    return response.data.data;
  });

  return (
    <div className={styles.Container}>
      <Navbar />
      <div className={styles.profileContainer}>
        <div className={styles.profile}>
          <div className={styles.profileHeading}>
            <h1>My Profile</h1>
          </div>
          <div className={styles.bio}>
            <div className={styles.bioImage}>
              {/* <img src={profile} alt="" /> */}
            </div>
            <div className={styles.bioData}>
              <div>
                <h1>Name</h1>
                <p>{profile?.users?.name}</p>
              </div>
              <div>
                <h1>Email</h1>
                <p>{state.user.email}</p>
              </div>
              <div>
                <h1>Phone</h1>
                <p>{profile?.phone ? profile?.phone : '-'}</p>
              </div>
              <div>
                <h1>Gender</h1>
                <p>{profile?.gender ? profile?.gender : '-'}</p>
              </div>
              <div>
                <h1>Address</h1>
                <p>
                  {profile?.address ? profile?.address : '-'}
                </p>
              </div>
            </div>
          </div>
        </div>
        <div className={styles.transaction}>
          <div className={styles.transactionHeading}>
            <h1>My Transaction</h1>
          </div>
          <div className={styles.transactionDetail}>
            {/* TD = Transaction Detail */}
            <div className={styles.TDLeft}>
              <div className={styles.TDImage}>
                {/* <img src={product} alt="" /> */}
              </div>
              <div className={styles.TDetails}>
                <div className={styles.TDTitle}>
                  <h1>{item.products.name}</h1>
                </div>
                <div className={styles.TDStock}>
                  <h2>{item.qty}</h2>
                </div>
                <div className={styles.TDPrice}>
                  <p>Price: Rp. {item.price}</p>
                </div>
                <div className={styles.TDTotal}>
                  <p>Sub Total: {item.price}</p>
                </div>
              </div>
            </div>
            <div className={styles.TDRight}>
              {/* <img src={logo} alt="" /> */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
