import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router';
import { useMutation } from 'react-query';
import { API } from '../../config/api'
import {UserContext} from '../../context/userContext'
import styles from "../../Css/category.module.css";
import Navbar from "../nav/navAdmin";

function addCategory() {

    const [state] = useContext(UserContext);

    const title = 'Add Product';
  document.title = 'DumbMerch | ' + title;

  let navigate = useNavigate();

  const [form, setForm] = seState({
    name: ''
  });
  
  const handleChange = (e) => {
    setForm({
      ...form
    });
  };

  
  const handleSubmit = useMutation(async (e) => {
    try {
      e.preventDefault();

      const config = {
        headers: {
          'Content-type': 'application/json',
        },
      };
      
      const formData = new FormData();
      formData.set('name', form.name);
  
      
      const response = await API.post('/category', formData, config);
      console.log(response)
  
      navigate('/category')
    } catch (error) {
      console.log(error);
    }
  });

  const checkAuth = () => {
    if (state.isLogin === true) {
      if(state.user.status == "user"){
        navigate('/')
      }
    }
  }
  checkAuth()

  return (
    <div className={styles.editContainer}>
      <Navbar />
      <div className={styles.editCatContainer}>
        <h1>Add category</h1>
        <form onSubmit={(e) => handleSubmit.mutate(e)}>
            <input type="text" onChange={handleChange()} placeholder="Category name..." />
            <button type="submit">Add</button>
        </form>
      </div>
    </div>
  );
}

export default addCategory;
