import "./Css/app.module.css";
import {useContext, useEffect} from 'react'
import {UserContext} from './context/userContext'
import { useNavigate, BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { UserContextProvider } from "./context/userContext";
import { QueryClient, QueryClientProvider } from "react-query";
// Pages
import Auth from "./Pages/user/Auth";
import Register from "./Pages/user/register";
import Home from "./Pages/user/Home";
import HomeAdmin from "./Pages/admin/HomeAdmin";
import Detail from "./Pages/user/DetailProduct";
import DetailAdmin from "./Pages/admin/DetailProductAdmin";
import Profile from "./Pages/user/Profile";
import Category from "./Pages/admin/CategoryAdmin";
import Product from "./Pages/admin/ProductAdmin";
import AddProduct from "./Components/product/addproduct"
import Complain from "./Pages/user/Complain";
import ComplainAdmin from "./Pages/admin/ComplainAdmin";
import Favicon from './Images/dmlogo.png';

import {API, setAuthToken} from "./config/api"

if(localStorage.token){
  setAuthToken(localStorage.token)
}

const client = new QueryClient();

function App() {

  let navigate = useNavigate();

  const [state, dispatch] = useContext(UserContext)

  console.log(state)

  useEffect(() =>{
    
    if(state.isLogin == false){
      navigate('/auth')
    }

  },[state])

  const checkAuth = async () => {
    try {

      const response = await API.get('/check-auth')

      if(response.status == 404){
        return dispatch({
          type: 'AUTH_ERROR'
        })
      }

      let payload = response.data.data.user
      payload.token = localStorage.token

      dispatch({
        type: 'USER_SUCCESS',
        payload
      })
      
    } catch (error) {
      console.log(error)
    }
  }

  // Call function check user with useEffect didMount here ...
  useEffect(() =>{
    checkAuth()
  },[])

  
const favicon = document.getElementById('idFavicon');
favicon.setAttribute('href', Favicon);

  return (
    <UserContextProvider>
      <QueryClientProvider client={client}>
        <Router>
          <Routes>
            {/* user */}
            <Route path="/" element={<Home />}></Route>
            <Route path="/auth" element={<Auth />}></Route>
            <Route path="/register" element={<Register />}></Route>
            <Route path="/detail" element={<Detail />}></Route>
            <Route path="/profile" element={<Profile />}></Route>
            <Route path="/complain" element={<Complain />}></Route>
            {/* admin */}
            <Route path="/home-admin" element={<HomeAdmin/>}></Route>
            <Route path="/detail-admin" element={<DetailAdmin />}></Route>
            <Route path="/complain-admin" element={<ComplainAdmin />}></Route>
            <Route path="/category" element={<Category />}></Route>
            <Route path="/product" element={<Product />}></Route>
            <Route path="/add-product" element={<AddProduct/>}></Route>
            <Route path="/complain" element={<Complain />}></Route>
          </Routes>
        </Router>
      </QueryClientProvider>
    </UserContextProvider>
  );
}

export default App;
