import styles from "../../Css/login.module.css";
import {useNavigate} from "react-router-dom"
import { useContext, useState } from 'react';
import { UserContext } from '../../context/userContext';

// Import useMutation from react-query here ...
import { useMutation } from 'react-query'

import { API, setAuthToken } from '../../config/api'

function LoginCard() {
  let navigate = useNavigate()

  const title = 'Login';
  document.title = 'DumbMerch | ' + title;

  const [state, dispatch] = useContext(UserContext);

  console.log(state)

  const [form, setForm] = useState({
    email: '',
    password: '',
  });

  const { email, password } = form;

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = useMutation(async (e) => {
    try {
      e.preventDefault();
  
      const config = {
        headers: {
          'Content-type': 'application/json',
        },
      };

      
  
      const body = JSON.stringify(form);
  
      const response = await API.post('/login', body, config);
  
      const userStatus = response.data.data.status

      const user = response.data.data

      dispatch({
        type: 'LOGIN_SUCCESS',
        payload: response.data.data,
      })
      console.log(payload)
      
      if(userStatus === 'admin'){
        navigate('/')
      } else if(userStatus === 'user' ){ 
        navigate('/complain-admin')
      }


      
    } catch (error) {
    }
  });

  
  return (
    <div className={styles.loginCard}>
      <h1>Login</h1>
      <form onSubmit={(e) => handleSubmit.mutate(e)}>
        <div className={styles.cardForm}>
          <input onChange={handleChange} name="email" type="text" value={email} placeholder="Email" />
          <input onChange={handleChange} name="password" value={password} type="password" placeholder="Password" />
          <button type="submit">Login</button>
        </div>
      </form>
    </div>
  );
}

export default LoginCard;
