import styles from "../../Css/detail.module.css";
import product1 from "../../Images/product1.jfif";
import Navbar from "../../Components/nav/nav";

function Detail() {
  let navigate = useNavigate();
  let { id } = useParams();

  let { data: product } = useQuery('productCache', async () => {
    const response = await API.get('/product/' + id);
    return response.data.data;
  });
  return (
    <div className={styles.container}>
      <Navbar />
      <div className={styles.detailContainer}>
        <div className={styles.productDetail}>
          <div className={styles.leftDetail}>
            <img src={product?.image} alt="Product" />
          </div>
          <div className={styles.rightDetail}>
            <div className={styles.productTitle}>
              <h1>KYT Falcon FR</h1>
            </div>
            <div className={styles.productStock}>
              <small>Stock: 235</small>
            </div>
            <div className={styles.productFeature}>
              <ul>
                <li>- Flat Visor</li>
                <li>- Full Face Helm</li>
                <li>- Bluetooth Compatible</li>
              </ul>
            </div>
            <div className={styles.productDesc}>
              <p>
                PT Tarakusuma Indah akhirnya resmi menghadirkan helm KYT Falcon
                FR terbaru pada 10 Maret 2021 kemarin… Helm full face terbaru
                dari KYT ini memiliki bayak sekali fitur dan spesifikasi yang
                sangat menggiurkan… Dari segi bandrol harga yang ditawarkan pun
                cukup menggoda karena masih dibawah angka satu juta Rupiah…
                Meskipun memiliki harga yang bisa dibilang cukup terjangkau,
                namun dari segi fitur sangat komplit, bahkan ada beberapa fitur
                trobosan terbaru untuk helm full face sekelasnya…
              </p>
            </div>
            <div className={styles.productPrice}>Rp.700.000</div>
          </div>
        </div>
      </div>
      <button>Buy</button>
    </div>
  );
}

export default Detail;
