import { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router";
import { useQuery, useMutation } from "react-query";
import { API } from "../../config/api";
import { useParams } from "react-router-dom";
import styles from "../../Css/profile.module.css";
import NavAdmin from "../nav/navAdmin";
import { UserContext } from "../../context/userContext";

function EditProfile() {
  const title = "Edit Profile";
  document.title = "DumbMerch | " + title;

  const [state] = useContext(UserContext);

  let navigate = useNavigate();
  const { id } = useParams();
  const [preview, setPreview] = useState(null); //For image preview
  const [profile, setProfile] = useState({}); //Store profile data
  const [form, setForm] = useState({
    image: "",
    phone: "",
    gender: "",
    address: "",
  });

  let { data: products, refetch } = useQuery("productCache", async () => {
    const response = await API.get("/profile/" + state.user.id);
    console.log(response.data.data);
    return response.data.data.profile;
  });

  // Handle change data on form
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]:
        e.target.type === "file" ? e.target.files : e.target.value,
    });

    // Create image url for preview
    if (e.target.type === "file") {
      let url = URL.createObjectURL(e.target.files[0]);
      setPreview(url);
    }
  };

  const handleSubmit = useMutation(async (e) => {
    try {
      e.preventDefault();

      const config = {
        headers: {
          "Content-type": "multipart/form-data",
        },
      };

      // Store data with FormData as object
      const formData = new FormData();
      formData.set("phone", form.phone);
      formData.set("gender", form.gender);
      formData.set("address", form.address);
      formData.set("image", form.image[0], form?.image[0].name);

      // Insert profile data
      const response = await API.patch(`/profile/` + id, formData, config);

      console.log(response.data);

      navigate("/profile");
    } catch (error) {
      console.log(error);
    }
  });

  useEffect(() => {
    if (profile) {
      setPreview(profile.image);
      setForm({
        ...form,
        name: profile.name,
        desc: profile.desc,
        price: profile.price,
        qty: profile.qty,
      });
      setProfile(profile);
    }
  }, [products]);

  return (
    <div>
      <div className={styles.editContainer}>
        <NavAdmin />
        <div className={styles.formContainer}>
          <p className={styles.editTitle}>Edit Profile</p>
          <form onSubmit={(e) => handleSubmit.mutate(e)}>
            {preview && (
              <div>
                <img
                  src={preview}
                  style={{
                    maxWidth: "9rem",
                    maxHeight: "150px",
                    objectFit: "cover",
                    marginBottom: "1rem",
                    borderRadius: "0.3rem",
                  }}
                  alt={preview}
                />
              </div>
            )}
            <label htmlFor="upImge">
              <p>Upload</p>
              <input
                type="file"
                id="upImge"
                name="image"
                onChange={handleChange}
                hidden
              />
            </label>
            <input
              type="text"
              onChange={handleChange}
              name="phone"
              placeholder="Phone"
              className="mt-3"
            ></input>
            <select
              aria-label="Default select example"
              onChange={handleChange}
              name="gender"
              className="mt-3"
            >
              <option hidden>Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>
            <input
              as="textarea"
              rows={3}
              onChange={handleChange}
              name="address"
              placeholder="Address"
              className="mt-3"
            ></input>
            <button type="submit">Save</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default EditProfile;
