import styles from "../../Css/product.module.css";
import Navbar from "../../Components/nav/navAdmin";
import {API} from "../../config/api"
import Modal from "../../Components/category/modal"
import {useQuery} from 'react-query'
import {useState} from "react"
import {useEffect, useMutation} from 'react'
import {Link,useNavigate} from 'react-router-dom'
import Edit from "../../Components/product/editproduct"

function Product() {
  const [modal, setModal] = useState(false)

  let navigate = useNavigate();

  const title = 'Product Admin';
  document.title = 'DumbMerch | ' + title;

  // Create variabel for id product and confirm delete data with useState here ...
  const [idDelete, setIdDelete] = useState(null);
  const [confirmDelete, setConfirmDelete] = useState(null);

  // const handleShow = () => setShow(true);

  let { data: products, refetch } = useQuery('productsCache', async () => {
    const response = await API.get('/products');
    console.log(response.data.data.products[0].image)
    return response.data.data.products
  });

  const addProduct = () => {
    navigate('/admin/add-product');
  };

  const handleEdit = (id) => {
    navigate(`/admin/edit-product/${id}`);
  };

  // Create function handle get id product & show modal confirm delete data here ...
  const handleDelete = (id) => {
    setIdDelete(id);
    handleShow();
  };


  // Create function for handle delete product here ...
  // If confirm is true, execute delete data
  const deleteById = useMutation(async (id) => {
    try {
      await API.delete(`/product/${id}`);
      refetch();
    } catch (error) {
      console.log(error);
    }
  });

  // Call function for handle close modal and execute delete data with useEffect here ...
  useEffect(() => {
    if (confirmDelete) {
      // Close modal confirm delete data
      handleClose();
      // execute delete data by id function
      deleteById.mutate(idDelete);
      setConfirmDelete(null);
    }
  }, [confirmDelete]);

  

  return (
    <div>
      <Navbar />
      <div className={styles.container}>
        <div className={styles.catContainer}>
          {products?.length !==0 && 
          <table className={styles.contentTable}>
          <thead>
            <tr>
              <th>No</th>
              <th>Photo</th>
              <th>Product Name</th>
              <th>Product Desc</th>
              <th>Price</th>
              <th>Qty</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
          {products?.map((item, index) => (
                <tr key={index}> 
                  <td>{index + 1}</td>
                  <td><a href={item.image} target="_blank">Click to see</a></td>
                  <td>{item.name}</td>
                  <td><div style={{
                    overflow: 'hidden',
                    height: '3.9rem',
                    width: '20rem'
                }}>{item.desc}</div></td>
                  <td>{item.price}</td>
                  <td>{item.qty}</td>
                  <td>
                  <Link to={`/update-product/` + item.id}>
                  <button className={styles.edit}>Edit</button>
                  </Link>
                  <button
                    className={styles.delete}
                    onClick={() => {
                      setModal(true);
                    }}
                  >
                    Delete
                  </button>
                </td>
                </tr>
              ))}
            </tbody> 
        </table>}
        </div>
      </div>
      {edit && <Edit/>}
      {modal && <Modal 
      setConfirmDelete={setConfirmDelete}
      show={show}
      handleClose={handleClose}
      closeModal={setModal}/>}
    </div>
  );
}

export default Product;
